export class TaskUpdateEvent extends CustomEvent {
    type = "update";
    constructor() {
        super("update");
    }
}
export class TaskMessageEvent extends CustomEvent {
    type = "message";
    constructor(message) {
        super("message", { detail: { message } });
    }
}
export class TaskErrorEvent extends CustomEvent {
    type = "error";
    constructor(message) {
        super("error", { detail: { message } });
    }
}
